INSERT INTO film(flim_name,film_name_real,film_trangthai,film_type,film_mota,film_tacgia,film_date_aired,film_views,film_quality,film_duration,film_rating,film_scores,flim_assessor,film_img,film_traller,film_current_episode,film_end_episode,film_showtimes) value ('Thục Sơn Kỳ Tiên Lục','Thục Sơn Kỳ Tiên Lục','Đang phát hành','3','Thời nhà Đường, biên giới hỗn loạn, Lý Hạo Bạch-chàng thanh niên đất Thục, ôm giấc mơ to lớn. Để thực hiện ước mơ cậu đã cùng Hồ Kiến tiến về Hàm Cốc Quan tham gia chiến trận. Yêu nghiệt nhân loạn thế gây rối, lão đạo sĩ Tiêu Dao Tử trừ yêu diệt quỷ khắp nơi. Mà ở Trường An xa xôi, lão đạo sĩ Tuần Quốc Sư lừa hoàng thượng, lợi dụng quyền thế bắt "Long Thú" để luyện tiên đơn. Lý Hạo Bạch vô tình dung hợp với "Li Châu" từ trong cơ thể của Long Thú, trở thành mục tiêu của biết bao kẻ. Không những là Tuần Quốc Sư muốn bắt mà còn nhiều người trên giang hồ muốn đoạt lấy Li Châu tu tiên. Trong lúc cùng đường, Lý Hạo Bạch được Tiêu Dao Tử ra tay cứu giúp, ông cảnh cáo Lý Hạo Bạch, nếu không lấy Li Châu ra, trả Li Châu lại cho long mạch trời đất. Cơ thể cậu phát nổ trong vòng 30 ngày. Đến lúc đó thiên hạ sẽ gặp tai họa lớn. Trên đường đến Hàm Cốc Quan cùng với Hồ Kiến, hai người gặp được A Y Mục - thiếu nữ lang thang người Tây vực. Ba người kết bạn, cùng nhau tiến đến Hàm Cốc Quan. Tiêu Dao Tử vì để bảo vệ Li Châu, cũng đành đi theo tùy cơ hành sự. Trên đường đi, 4người nhiều lần thoát khỏi truy sát của thuộc hạ Tuần Quốc Sư. Trong lúc đó, Li Châu cũng dần hợp thể với Lý Hạo Bạch, cậu vô tình dị hóa thành "Hắc Long". Bạch Long tượng trưng cho "mặt trời" bỗng xuất hiện, bám lấy Lý Hạo Bạch. Bạch Long cố tìm làm cho Tiêu Dao Tử rời khỏi Lý Bạch Hạo để ông truy tìm bí ẩn về Bạch Long. Tin tức về Li Châu truyền khắp thiên hạ, có Li Châu trong mình, Lý Hạo Bạch bị kỳ nhân dị sĩ khắp giang hồ đuổi giết.','đang cập nhật','2021','0','HD','24','4.9','9','','https://animehay.club/upload/poster/3389-1638631627.jpg','','2','??','T2 Mỗi tuần');INSERT INTO film(f